import React, { useState } from "react";
import { QRCodeCanvas } from "qrcode.react";  // library for QR code

function FarmerForm() {
  const [herb, setHerb] = useState("");
  const [location, setLocation] = useState(null);
  const [submittedData, setSubmittedData] = useState(null);

  // Get GPS location
  const getLocation = () => {
    navigator.geolocation.getCurrentPosition((pos) => {
      setLocation({
        lat: pos.coords.latitude,
        lng: pos.coords.longitude,
      });
    });
  };

  // Submit herb details (no backend, just store locally)
  const handleSubmit = (e) => {
    e.preventDefault();
    const data = { herb, location };
    setSubmittedData(data); // save data to show in QR
    alert("‚úÖ Herb logged successfully!");
  };

  return (
    <div className="p-4 bg-gray-100 rounded">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Herb Name"
          value={herb}
          onChange={(e) => setHerb(e.target.value)}
          className="border p-2 m-2"
          required
        />
        <button
          type="button"
          onClick={getLocation}
          className="bg-blue-500 text-white px-3 py-1 m-2"
        >
          Get Location
        </button>
        {location && <p>üìç Lat: {location.lat}, Lng: {location.lng}</p>}
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 m-2"
        >
          Submit
        </button>
      </form>

      {/* Show QR code after submit */}
      {submittedData && (
        <div className="mt-4">
          <h3>Generated QR Code:</h3>
          <QRCodeCanvas
            value={JSON.stringify(submittedData)}
            size={200}
          />
        </div>
      )}
    </div>
  );
}

export default FarmerForm;
