import React, { useState } from "react";
import { QRCodeSVG } from "qrcode.react";

function AdminDashboard() {
  // Dummy herb data with "status"
  const [herbs, setHerbs] = useState([
    { id: 1, name: "Tulsi", farmer: "Ramesh", lat: 12.9255, lng: 74.8407, status: "pending" },
    { id: 2, name: "Ashwagandha", farmer: "Sita", lat: 13.0123, lng: 77.5544, status: "pending" },
    { id: 3, name: "Neem", farmer: "Kiran", lat: 12.8765, lng: 74.6543, status: "pending" },
  ]);

  // Approve function
  const approveHerb = (id) => {
    setHerbs(
      herbs.map((herb) =>
        herb.id === id ? { ...herb, status: "approved" } : herb
      )
    );
  };

  // Reject function
  const rejectHerb = (id) => {
    setHerbs(
      herbs.map((herb) =>
        herb.id === id ? { ...herb, status: "rejected" } : herb
      )
    );
  };

  // Function to download QR as PNG
  const downloadQR = (herb) => {
    const svg = document.getElementById(`qr-${herb.id}`);
    if (!svg) return;

    const serializer = new XMLSerializer();
    const svgData = serializer.serializeToString(svg);

    const canvas = document.createElement("canvas");
    const ctx = canvas.getContext("2d");

    const img = new Image();
    img.onload = function () {
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);

      const pngFile = canvas.toDataURL("image/png");
      const downloadLink = document.createElement("a");
      downloadLink.download = `${herb.name}-qr.png`;
      downloadLink.href = pngFile;
      downloadLink.click();
    };

    img.src =
      "data:image/svg+xml;base64," +
      btoa(unescape(encodeURIComponent(svgData)));
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">üåø Admin Dashboard</h1>

      <table className="table-auto border-collapse border border-gray-400 w-full text-center">
        <thead>
          <tr className="bg-gray-200">
            <th className="border border-gray-400 px-4 py-2">ID</th>
            <th className="border border-gray-400 px-4 py-2">Herb Name</th>
            <th className="border border-gray-400 px-4 py-2">Farmer</th>
            <th className="border border-gray-400 px-4 py-2">Location</th>
            <th className="border border-gray-400 px-4 py-2">Status</th>
            <th className="border border-gray-400 px-4 py-2">QR Code</th>
            <th className="border border-gray-400 px-4 py-2">Download</th>
          </tr>
        </thead>
        <tbody>
          {herbs.map((herb) => (
            <tr key={herb.id}>
              <td className="border border-gray-400 px-4 py-2">{herb.id}</td>
              <td className="border border-gray-400 px-4 py-2">{herb.name}</td>
              <td className="border border-gray-400 px-4 py-2">{herb.farmer}</td>
              <td className="border border-gray-400 px-4 py-2">
                {herb.lat}, {herb.lng}
              </td>
              <td className="border border-gray-400 px-4 py-2">
                {herb.status === "approved" ? (
                  <span className="text-green-600 font-bold">Approved ‚úÖ</span>
                ) : herb.status === "rejected" ? (
                  <span className="text-red-600 font-bold">Rejected ‚ùå</span>
                ) : (
                  <div className="flex gap-2 justify-center">
                    <button
                      className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                      onClick={() => approveHerb(herb.id)}
                    >
                      Approve
                    </button>
                    <button
                      className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700"
                      onClick={() => rejectHerb(herb.id)}
                    >
                      Reject
                    </button>
                  </div>
                )}
              </td>

              {/* Show QR only if approved */}
              <td className="border border-gray-400 px-4 py-2">
                {herb.status === "approved" && (
                  <QRCodeSVG
                    id={`qr-${herb.id}`}
                    value={JSON.stringify(herb)}
                    size={80}
                  />
                )}
              </td>

              {/* Download button only if approved */}
              <td className="border border-gray-400 px-4 py-2">
                {herb.status === "approved" && (
                  <button
                    className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                    onClick={() => downloadQR(herb)}
                  >
                    Download QR
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminDashboard;
